import React, {useState} from "react";
import {Box, Button, TextField, Icon, Typography} from "@mui/material";
import {TodoItem} from "./TodoItem";

const App = () => {
    const [inputText, setInputText] = useState('');
    const [todos, setTodos] = useState([]);

    const onInputChanged = (event) => {
        setInputText(event.target.value);
    }

    const onAddButtonClicked = () => {
        if (inputText !== "") {
            setTodos([...todos, inputText]);
            setInputText('');
        }
    }

    const onDeleteButtonClicked = (deleteButtonIndex) => {
        setTodos(todos.filter((_, todoArrIndex) => todoArrIndex !== deleteButtonIndex));
    }

    const mapTodoToComponent = (todo, index) => (
        <TodoItem
            key={index}
            value={todo}
            onDelete={() => onDeleteButtonClicked(index)}
            onEdit={(updatedValue) => onTodoUpdated(updatedValue, index)}
        />
    );
    const onTodoUpdated = (newTodoValue, index) =>
    {
        const newTodos = todos;
        newTodos[index] = newTodoValue;
        setTodos(newTodos);
    }

    return (
        <Box>
            <TextField
                label="New todos"
                variant="outlined"
                value={inputText}
                onChange={onInputChanged}
            />
            <Button
                variant="contained"
                onClick={onAddButtonClicked}
            >Add</Button>
            {
                todos.map(mapTodoToComponent)
            }
        </Box>
    )
}

export default App;
